cleanup_vars () 
{ 
    unset opt;
    unset gettingopt;
    unset OPTARG;
    unset OPTIND;
    unset redhat;
    unset result;
    unset setup_location;
    unset release;
    unset build;
    unset AUSER;
    unset kernel;
    unset sl;
    unset time;
    unset tempdate;
    unset reldate;
    unset compdate;
    unset errorcode;
    unset retval_os;
    unset retval_rel;
    unset retval_args;
    unset retval_g4;
    unset expath;
    unset s5path;
    unset s6path;
    unset g4_version
}
echo_setup_vars () 
{ 
    if [ -t 1 ]; then
        if [ -z "${_CONDOR_SCRATCH_DIR}" ]; then
            local build_date_file=${SRT_PUBLIC_CONTEXT}/build_date;
            if [ -f $build_date_file ]; then
                echo "" 1>&2;
                echo -e "Release:    \e[01;34m$SRT_BASE_RELEASE\e[0m" 1>&2;
                echo -e "Build:      \e[01;34m$SRT_QUAL\e[0m" 1>&2;
                echo -e "Build Date: \e[01;34m`cat $build_date_file`\e[0m \n" 1>&2;
            else
                echo "" 1>&2;
                echo -e "Release: \e[01;34m$SRT_BASE_RELEASE\e[0m" 1>&2;
                echo -e "Build:   \e[01;34m$SRT_QUAL\e[0m \n" 1>&2;
            fi;
        fi;
    fi
}
export_g4_var () 
{ 
    g4_version=v4_10_1_p03;
    if [ -n "${GEANT4_VERSION}" ]; then
        if [ "${GEANT4_VERSION}" == "${g4_version}" ]; then
            export G4NEUTRONHP_USE_ONLY_PHOTONEVAPORATION=1;
        fi;
    else
        echo -e "\e[01;31mError: GEANT4_VERSION env. variable not set. Something is wrong here.\e[0m";
        echo -e "\e[01;31mSetup aborting.\e[0m";
        return 1;
    fi;
    return 0
}
main () 
{ 
    if [[ ${NOVASOFT_SETUP} = 1 ]]; then
        echo;
        echo "***********************WARNING!***********************";
        echo "Currently we prevent multiple sourcing, i.e. switching";
        echo "between releases mid-session!";
        echo "You have NOT successfully sourced a different release!";
        echo "If you want to source a different release you must either";
        echo "log out and login to source again, or open a new terminal!";
        echo "This is to prevent indefinite extension of PATH and ";
        echo "LD_LIBRARY_PATH";
        echo "********************END OF WARNING!********************";
        echo;
        return 1;
    fi;
    set_defaults;
    process_os;
    retval_os=$?;
    if [ "$retval_os" == 1 ]; then
        cleanup_vars;
        return 1;
    fi;
    process_args $*;
    retval_args=$?;
    if [ "$retval_args" == 1 ]; then
        cleanup_vars;
        return 1;
    fi;
    IFS=':' read -ra tmppath <<< "${expath}";
    for ipath in "${tmppath[@]}";
    do
        if [ -e "${ipath}/setup" ]; then
            upssetup="${ipath}/setup";
            break;
        fi;
    done;
    if [ -z ${upssetup} ]; then
        echo "No setup script found in ${expath}";
        echo -e "\e[01;31mInvalid PRODUCTS path. Setup has failed.\e[0m";
        cleanup_vars;
        return 1;
    fi;
    export PRODUCTS=${expath};
    export EXTERNALS=`dirname ${upssetup}`;
    process_release;
    retval_rel=$?;
    if [ "$retval_rel" == 1 ]; then
        cleanup_vars;
        return 1;
    fi;
    if [ $os == "slf6" ] && [ $time == "renaissance" ]; then
        if [ -f ${s6path}/srt/srt.sh ]; then
            source ${s6path}/srt/srt.sh;
        else
            echo -e "\e[01;31m${s6path}/srt/srt.sh does not exist. Setup has failed.\e[0m";
            cleanup_vars;
            return 1;
        fi;
    else
        if [ -f ${s5path}/srt/srt.sh ]; then
            source ${s5path}/srt/srt.sh;
        else
            echo -e "\e[01;31m${s5path}/srt/srt.sh does not exist. Setup has failed.\e[0m";
            cleanup_vars;
            return 1;
        fi;
    fi;
    source_setup_script $*;
    retval_rel=$?;
    if [ "$retval_rel" == 1 ]; then
        cleanup_vars;
        return 1;
    fi;
    export_g4_var;
    retval_g4=$?;
    if [ "$retval_g4" == 1 ]; then
        cleanup_vars;
        unset G4NEUTRONHP_USE_ONLY_PHOTONEVAPORATION;
        return 1;
    fi;
    export NOVASOFT_SETUP=1;
    if [ $time == "medieval" ] || [ $time == "renaissance" ]; then
        source $SRT_PUBLIC_CONTEXT/setup/setup_novagrid.sh -r ${release} -b ${build} ${shell_type};
        if [ ! -d /grid/fermiapp/ ]; then
            source $NOVAGRIDUTILS_DIR/bin/setup_shim;
        fi;
    fi;
    nutools_premove=v2_02_00;
    if version_gt $NUTOOLS_VERSION $nutools_premove; then
        echo nutools $NUTOOLS_VERSION is greater than $nutools_premove\n;
        echo The version before the move of checkClassVersion;
        echo Setting up cetbuildtools;
        gives access to checkClassVersion;
        source setup_cetbuildtools.sh;
        echo "`which checkClassVersion`";
    else
        echo "nutools version $NUTOOLS_VERSION NOT GREATER THAN $nutools_premove; DO NOTHING";
        echo "`which checkClassVersion`";
    fi;
    echo_setup_vars;
    echo "PWD: $PWD";
    run_datagram;
    nuke_tmp;
    setup_nova_bashcompletion;
    setup_ccache;
    cleanup_vars
}
nuke_tmp () 
{ 
    if [[ $(hostname -s) =~ ^novagpvm.*[0-9]+$ ]]; then
        find /tmp -user $USER ! -name "x509up*" ! -name "krb5cc*" -mmin +30 -delete &>/dev/null;
        find /tmp -user $USER -name "x509up*" -mtime +7 -delete &>/dev/null;
        find /var/tmp -user $USER -mtime +7 -exec rm -rf {} \; &>/dev/null;
    fi
}
process_args () 
{ 
    errorcode=0;
    while getopts "hcse:r:b:5:6:n:-:" opt; do
        if [ "$opt" = "-" ]; then
            opt=$OPTARG;
        fi;
        gettingopt=1;
        case $opt in 
            h | help)
                usage;
                errorcode=1
            ;;
            s | sh)
                shell_type="-s"
            ;;
            c | csh)
                shell_type="-c"
            ;;
            r | release)
                release=$OPTARG
            ;;
            b | build)
                build=$OPTARG
            ;;
            e)
                expath=$OPTARG
            ;;
            5)
                s5path=$OPTARG
            ;;
            6)
                s6path=$OPTARG
            ;;
            n)
                setup_novasoft_setup_script=$OPTARG
            ;;
            ?)
                usage;
                errorcode=1
            ;;
        esac;
    done;
    if [ "$#" != "0" ] && [ "$gettingopt" != "1" ] && [ "$errorcode" != "1" ]; then
        echo -e "\e[01;31mERROR! Invalid argument/option. Try again!\e[0m" 1>&2;
        usage;
        errorcode=1;
    fi;
    if [[ ${release} == N* ]] || [[ ${release} == "development" ]]; then
        s5path=/cvmfs/nova-development.opensciencegrid.org/novasoft;
        s6path=/cvmfs/nova-development.opensciencegrid.org/novasoft;
    fi;
    return $errorcode
}
process_os () 
{ 
    redhat=`cat /etc/redhat-release`;
    if [[ "$redhat" =~ "release 5." ]]; then
        os=slf5;
    else
        if [[ "$redhat" =~ "release 6." ]]; then
            os=slf6;
        else
            echo "Unsupported platform. NOvASoft is only available for SLF5 and SLF6 distributions.";
            return 1;
        fi;
    fi;
    return 0
}
process_release () 
{ 
    if [ ${release} == "development" ]; then
        time="renaissance";
    else
        if [ ${release} == "first-ana" ]; then
            time="renaissance";
        else
            tempdate=`echo $release | sed 's/[a-zA-Z.]//g'`;
            tempdate=${tempdate:0:8};
            numdate=${tempdate//[^0-9]/};
            if [ ${#numdate} -ne 6 ]; then
                time="classical";
                return;
            fi;
            reldate=$(date -d $tempdate +"%y%m%d") || return 1;
            sl6compdate=$(date -d 14-07-31 +"%y%m%d");
            gridcompdate=$(date -d 14-03-31 +"%y%m%d");
            if [ $reldate -ge $sl6compdate ]; then
                time="renaissance";
            else
                if [ $reldate -ge $gridcompdate ]; then
                    time="medieval";
                else
                    time="classical";
                fi;
            fi;
        fi;
    fi
}
run_datagram () 
{ 
    $SRT_DIST/setup/datagram/datagram_client.py "${BFCURRENT} ${build}"
}
set_defaults () 
{ 
    shell_type="-s";
    release=development;
    build=debug;
    os=slf5;
    expath=/grid/fermiapp/products/nova/externals:/nusoft/app/externals:/grid/fermiapp/products/common/db:/nova/data/pidlibs/products;
    s5path=/grid/fermiapp/nova/novaart/novasvn;
    s6path=/nova/app/home/novasoft/slf6/novasoft;
    setup_novasoft_setup_script=setup_novasoft_setup.sh;
    unset INVALID_RELEASE
}
setup () 
{ 
    . `/cvmfs/nova.opensciencegrid.org/externals/ups/v5_2_0/Linux64bit+2.6-2.12/bin/ups setup "$@"`
}
setup_ccache () 
{ 
    export CCACHE_MAXSIZE=50G;
    export CCACHE_UMASK=002;
    export CCACHE_SLOPPINESS=file_macro,time_macros;
    export CCACHE_TEMPDIR=/tmp;
    if [[ `hostname -s` == novagpvm?? ]]; then
        export CCACHE_DIR=/nova/app/home/novasoft/ccache_cache/;
        export CCACHE_BASEDIR=/nova/app/users/`whoami`;
        export PATH=$CCACHE_BIN_DIR:$PATH;
    fi
}
setup_nova_bashcompletion () 
{ 
    if [ "$NOVA_BASH_COMPLETION" = true ] && [ -e $SRT_PUBLIC_CONTEXT/setup/nova_bashcompletion ]; then
        source $SRT_PUBLIC_CONTEXT/setup/nova_bashcompletion;
    fi
}
source_setup_script () 
{ 
    setup_location=$SRT_DIST/setup;
    if [ ! -d ${SRT_DIST}/releases/${release} ]; then
        echo -e "\e[01;31mINVALID RELEASE. Try sourcing again with a valid release name.\e[0m" 1>&2;
        return 1;
    fi;
    if [ ${build} != "debug" ] && [ ${build} != "maxopt" ]; then
        echo -e "\e[01;31mINVALID BUILD. Valid options are 'debug' and 'maxopt'.\e[0m" 1>&2;
        echo "Try sourcing again with a valid build" 1>&2;
        return 1;
    fi;
    setup_script=${SRT_DIST}/releases/${release}/setup/${setup_novasoft_setup_script};
    if [ ! -x ${setup_script} ]; then
        echo -e "\e[01;31mERROR: setup_script ${setup_script} does not exist or is not executable.\e[0m" 1>&2;
        return 1;
    fi;
    result=`${setup_script} ${shell_type} -r ${release} -b ${build}`;
    if [ -r "$result" ]; then
        source ${result};
    fi;
    if [ -x ${SRT_PUBLIC_CONTEXT}/bin/${SRT_SUBDIR}/srt_environment_nova ]; then
        function srt_setup () 
        { 
            source `srt_environment_nova -X "$@"`
        };
    fi;
    return 0
}
srt_setup () 
{ 
    source `srt_environment_nova -X "$@"`
}
unsetup () 
{ 
    . `/cvmfs/nova.opensciencegrid.org/externals/ups/v5_2_0/Linux64bit+2.6-2.12/bin/ups unsetup "$@"`
}
unsetup_all () 
{ 
    eval `ups active | sed -e 1d -e "/^ups /d" -e "s/^\([^ ]*\)/echo unsetup -j \1;unsetup -j \1/" -e "s/\(.*\)/\1;/"`
}
usage () 
{ 
    echo "" 1>&2;
    echo "usage: setup_nova.sh [options]" 1>&2;
    echo "options:" 1>&2;
    echo "     -h:            prints this usage message                    " 1>&2;
    echo "     -c:            Use c-shell                                  " 1>&2;
    echo "     -s:            Use sh-shell (standard)                      " 1>&2;
    echo "     -r:            specifies the release to be set up           " 1>&2;
    echo "     -b:            specifies the build (debug/prof/maxopt)      " 1>&2;
    echo "     -e:            user-defined PRODUCTS path                  " 1>&2;
    echo "     -5:            user-defined \$SRT_DIST path for slf5 build   " 1>&2;
    echo "     -6:            user-defined \$SRT_DIST path for slf6 build   " 1>&2;
    echo "     -n:            user-defined setup_novasoft_setup equivalent " 1>&2;
    echo "                                                                 " 1>&2;
    echo "You either requested the help or setup has failed. Try again!    " 1>&2;
    echo "                                                                 " 1>&2;
    return 1
}
version_gt () 
{ 
    test "$(echo "$@" | tr " " "\n" | sort -V | head -n 1)" != "$1"
}
